sys_ui_script.script:
class GameOfLifeApp extends ReactorComponent
{
	constructor(props)
	{
		super(props);
	}

	render()
	{
		var strStartPattern = this.props.startpattern;
		Log('GameOfLifeApp: startpattern: '+strStartPattern);
		if (strStartPattern == '')
		{
			strStartPattern = 'gosperglider';
		}
		return e(GameOfLifeController,
			{
				startpattern: strStartPattern,
				addrandompattern: 0
			});
	}	
}
