x_snc_reactor_component.script:
class GameOfLifeController extends ReactorComponent
{
	constructor(props)
	{
		super(props);
		//this.state = { clicked: false };
		//this.onClick = this.onClick.bind(this);

		var nGameWidth = 50; 
		var nGameHeight = 30;
		var nCellSize = 20; 
		var nSpeed = 80;

		this.state =
		{
			width: nGameWidth,
			height: nGameHeight,
			cellsize: nCellSize,
			speed: nSpeed,
			generation: 0
		};


	}

	/*
	onClick()
	{
		this.setState( {clicked: true} );
		if (typeof this.props.callbackOnClick == 'function')
		{
			this.props.callbackOnClick();
		}
	}
	*/

	componentDidMount()
	{
		this.timerID = setInterval(
			() => this.onNextGeneration(),
			80
		);
	}

	componentWillUnmount()
	{
		clearInterval(this.timerID);
	}

	onNextGeneration()
	{
		var aNewGameState = GameOfLifePlay(
			this.state.height,
			this.state.width,
			this.state.gamestate);
		this.setState(
			{
				generation: this.state.generation+1,
				gamestate: aNewGameState
			});
	}

	render()
	{

		var aGameState = [];
		aGameState.push([0,0,1,0,0,0,0]);     		
		aGameState.push([0,0,0,1,0,0,0]);
		aGameState.push([0,1,1,1,0,0,0]);
		aGameState.push([0,0,0,0,0,0,0]);
		aGameState.push([0,0,0,0,0,0,0]);
		aGameState.push([0,0,0,0,0,0,0]);     
		aGameState.push([0,0,0,0,0,0,0]);  	

		return e(GameOfLifeBoard,
			{
				height: nHeight,
				width: nWidth,
				cellsize: nCellSize,
				gamestate: aGameState
			});	
	}	
}
